import fruits from "./foods";

function choice(items) {
  let item = items[Math.floor(Math.random() * items.length)];
  console.log("I'd like one " + item + ", please");
  return item;
}

function remove(items, item) {
  var itemsNew = items;
  itemsNew.remove(item);
  return itemsNew;
}

export { choice, remove };
